// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract CredentialVerification {

    struct Credential {
        address issuer;
        string credentialHash;
        uint256 timestamp;
    }

    mapping(address => Credential[]) private credentials;

    event CredentialIssued(address indexed holder, string credentialHash, uint256 timestamp);

    modifier onlyIssuer() {
        require(msg.sender != address(0), "Not authorized as issuer.");
        _;
    }

    function issueCredential(address _holder, string memory _credentialHash) public onlyIssuer {
        require(_holder != address(0), "Invalid holder address.");
        Credential memory newCredential = Credential({
            issuer: msg.sender,
            credentialHash: _credentialHash,
            timestamp: block.timestamp
        });

        credentials[_holder].push(newCredential);

        emit CredentialIssued(_holder, _credentialHash, block.timestamp);
    }

    function getCredentials(address _holder) public view returns (Credential[] memory) {
        return credentials[_holder];
    }

    function verifyCredential(address _holder, string memory _credentialHash) public view returns (bool) {
        Credential[] memory holderCredentials = credentials[_holder];

        for (uint256 i = 0; i < holderCredentials.length; i++) {
            if (keccak256(abi.encodePacked(holderCredentials[i].credentialHash)) == keccak256(abi.encodePacked(_credentialHash))) {
                return true;
            }
        }

        return false;
    }
}
